mapper.select:
select 

from contribute
where full_name = #{fullName,jdbcType=CHAR}
  and login = #{login,jdbcType=CHAR}

mapper.delete:
delete from contribute
where full_name = #{fullName,jdbcType=CHAR}
  and login = #{login,jdbcType=CHAR}

mapper.insert:
insert into contribute (full_name, login, line
  )
values (#{fullName,jdbcType=CHAR}, #{login,jdbcType=CHAR}, #{line,jdbcType=INTEGER}
  )

mapper.update:
update contribute


    line = #{line,jdbcType=INTEGER},


where full_name = #{fullName,jdbcType=CHAR}
  and login = #{login,jdbcType=CHAR}

update contribute
set line = #{line,jdbcType=INTEGER}
where full_name = #{fullName,jdbcType=CHAR}
  and login = #{login,jdbcType=CHAR}
