Set @rank:=0;
Select rank
From (
Select full_name, @rank:=@rank+1 As rank
From repository
Order By (forks_count+stargazers_count+watchers_count)
) tb
where full_name = #{full_name};

Set @rank:=0;
Select rank
From (
Select full_name, @rank:=@rank+1 As rank
From repository
WHERE language IN (
SELECT language FROM repository
WHERE full_name = #{full_name}
)
Order By size
) tb
where full_name = #{full_name};

Set @rank:=0;
Select rank
From (
Select full_name, @rank:=@rank+1 As rank
From repository
Order By updated_at
) tb
where full_name = #{full_name};

Set @rank:=0;
Select rank/(SELECT count(DISTINCT full_name) FROM contribute)
From (
Select full_name, @rank:=@rank+1 As rank
From contribute
Group By full_name
Order By count(*)
) tb
where full_name = #{full_name};

Set @rank:=0;
Select rank
From (
Select full_name, @rank:=@rank+1 As rank
From repository
Order By (open_issues_count+forks_count*3)
) tb
where full_name = #{full_name};

